mapper.select:
select count(tas.oid) from t_assess_score as tas left join t_assess_range as tar on tar.user_id = tas.user_id
AND tar.year_id = #{yearId,jdbcType=BIGINT}
left join t_user as tu on tu.oid = tas.user_id left join t_dept as td on td.oid = tar.dept_id where 1=1
AND tas.year_id = #{yearId,jdbcType=BIGINT}
AND tar.dept_id = #{deptId,jdbcType=BIGINT}
AND tu.user_name LIKE CONCAT(CONCAT('%',#{key}),'%')

select tas.*, tu.user_name, tu.user_type, ty.year_name, td.dept_name from t_assess_score as tas left join t_assess_range as tar on tar.user_id = tas.user_id
AND tar.year_id = #{yearId,jdbcType=BIGINT}
left join t_user as tu on tu.oid = tas.user_id left join t_year as ty on ty.oid = tas.year_id left join t_dept as td on td.oid = tar.dept_id where 1=1
AND tas.year_id = #{yearId,jdbcType=BIGINT}
AND tar.dept_id = #{deptId,jdbcType=BIGINT}
AND tu.user_name LIKE CONCAT(CONCAT('%',#{key}),'%')
order by IFNULL(tas.adjust_score, tas.score) desc
limit #{start},#{length}

mapper.update:
update t_assess_score
set adjust_score = #{adjustScore,jdbcType=INTEGER}
where oid = #{oid,jdbcType=BIGINT}

update t_assess_score
set adjust_qzcp = #{adjustQzcp,jdbcType=INTEGER}
where oid = #{oid,jdbcType=BIGINT}
