mapper.select:
select tc.oid, tc.stu_id as stuId, tc.name,tc.level, tc.obtain_date as obtainDate, tc.unit_name as unitName, tc.rank, tc.status ,stu.name as stuName from t_contest as tc left join t_student stu on tc.stu_id = stu.id where 1=1
AND tc.name LIKE CONCAT(CONCAT('%',#{searchDTO.name}),'%')
AND tc.level = #{searchDTO.level}
AND stu.name LIKE CONCAT(CONCAT('%',#{searchDTO.stuName}),'%')
order by tc.oid desc
limit #{pageData.start},#{pageData.size}

mapper.insert:
insert into t_contest ( oid, stu_id, name, level, obtain_date,unit_name, rank, status)
values (
         #{oid,jdbcType=BIGINT}
       , #{stuId,jdbcType=INTEGER}
       , #{name,jdbcType=VARCHAR}
       , #{level,jdbcType=INTEGER}
       , #{obtainDate,jdbcType=TIMESTAMP}
       , #{unitName,jdbcType=VARCHAR}
       , #{rank,jdbcType=VARCHAR}
       , #{status,jdbcType=INTEGER}
       )
