mapper.update:
update t_faculty
set faculty_name = #{facultyName,jdbcType=VARCHAR},
  faculty_code = #{facultyCode,jdbcType=VARCHAR},
  faculty_admin = #{facultyAdmin,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where oid = #{oid,jdbcType=INTEGER}

mapper.select:
SELECT td.*, tt.name AS admin_name FROM t_faculty AS td LEFT JOIN t_teacher AS tt ON tt.id = td.faculty_admin WHERE 1=1
AND td.faculty_name LIKE CONCAT(CONCAT('%',#{key}),'%')
ORDER BY td.create_time DESC limit #{start},#{length}

SELECT

FROM t_faculty AS td
WHERE td.faculty_name = #{facultyName}

  AND td.oid != #{oid}

limit 1
