mapper.insert:
insert into t_menu (oid, menu_name, menu_code, 
  link, icon, parent_id
  )
values (#{oid,jdbcType=BIGINT}, #{menuName,jdbcType=VARCHAR}, #{menuCode,jdbcType=VARCHAR}, 
  #{link,jdbcType=VARCHAR}, #{icon,jdbcType=VARCHAR}, #{parentId,jdbcType=BIGINT}
  )

mapper.select:
select tm.* from t_menu as tm where 1=1
AND tm.parent_id = #{parentId,jdbcType=BIGINT}
order by tm.oid asc
limit #{start},#{length}
