mapper.insert:
insert into t_project_rule (oid, type_id, item_code, item_name, rule_detail, rule_class)
values (#{oid,jdbcType=BIGINT}
, #{typeId,jdbcType=BIGINT}
, #{itemCode,jdbcType=VARCHAR}
, #{itemName,jdbcType=VARCHAR}
, #{ruleDetail,jdbcType=VARCHAR}
, #{ruleClass,jdbcType=VARCHAR}
)

mapper.select:
select count(td.oid) from t_project_rule as td where 1=1
AND td.type_id = #{typeId}
AND td.item_name LIKE CONCAT(CONCAT('%',#{key}),'%')

select td.* from t_project_rule as td where 1=1
AND td.type_id = #{typeId}
AND td.item_name LIKE CONCAT(CONCAT('%',#{key}),'%')
order by td.oid desc
limit #{start},#{length}

mapper.delete:
delete from t_project_rule
where oid in

  #{id}
