mapper.select:
select ras.id, y.year_name, first.name as first_level_name, second.name as second_level_name, three.name as three_level_name, gi.name as grow_name, g.grade_name, c.name as class_name, s.name as student_name, s.student_no, s.id_card, ras.student_id, ras.score, ras.create_time from t_rec_add_score ras left join t_growth_item gi on gi.id = ras.grow_id left join t_growth first on first.id = gi.first_level_id left join t_growth second on second.id = gi.second_level_id left join t_growth three on three.id = gi.three_level_id left join t_student s on s.id = ras.student_id left join t_year y on y.oid = ras.year_id left join t_grade g on g.oid = s.grade_id left join t_class c on c.id = s.class_id
and y.oid = #{query.yearId}
and gi.first_level_id = #{query.firstLevelId}
and gi.second_level_id = #{query.secondLevelId}
and gi.three_level_id = #{query.threeLevelId}
and gi.name like concat('%',#{query.growName},'%')
and g.oid = #{query.gradeId}
and c.name like concat('%',#{query.className},'%')
and s.name like concat('%',#{query.studentName},'%')
and s.student_no like concat('%',#{query.studentNo},'%')
and ras.create_time between #{query.startTime} and #{query.endTime}
order by ras.create_time

select ras.id, y.year_name, first.name as first_level_name, second.name as second_level_name, three.name as three_level_name, gi.name as grow_name, ras.score, ras.create_time from t_rec_add_score ras left join t_growth_item gi on gi.id = ras.grow_id left join t_growth first on first.id = gi.first_level_id left join t_growth second on second.id = gi.second_level_id left join t_growth three on three.id = gi.three_level_id left join t_year y on y.oid = ras.year_id
ras.student_id = #{studentId}
and y.oid = #{query.yearId}
and gi.first_level_id = #{query.firstLevelId}
and gi.second_level_id = #{query.secondLevelId}
and gi.three_level_id = #{query.threeLevelId}
and gi.name like concat('%',#{query.growName},'%')
and ras.create_time between #{query.startTime} and #{query.endTime}
order by ras.create_time desc

select *
from t_rec_add_score
where create_time between #{startTime} and #{endTime}

select *
from t_rec_add_score
where grow_id = #{growId}
  and create_time between #{startTime} and #{endTime}

select sum(score) as totalScore
from t_rec_add_score
where
year_id = #{yearId}

    and student_id = #{studentId}


    and grow_id in

        #{growthItemId}
