mapper.update:
update t_standard_category
set category_name = #{categoryName,jdbcType=VARCHAR},
  category_score = #{categoryScore,jdbcType=INTEGER},
  create_user = #{createUser,jdbcType=BIGINT},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where oid = #{oid,jdbcType=BIGINT}

mapper.select:
SELECT tsc.* FROM t_standard_category AS tsc WHERE 1=1
AND tsc.category_name = #{categoryName,jdbcType=VARCHAR}
AND tsc.oid != #{oid,jdbcType=BIGINT}
ORDER BY tsc.oid DESC LIMIT 1

mapper.delete:
delete from t_standard_category
where oid in

  #{id}
