mapper.select:
select count(tsn.oid) from t_standard_norm as tsn where 1=1
AND tsn.category_id = #{categoryId,jdbcType=BIGINT}
AND tsn.norm_name LIKE CONCAT(CONCAT('%',#{key}),'%')

select tsn.*, tsc.category_name from t_standard_norm as tsn left join t_standard_category tsc on tsc.oid = tsn.category_id where 1=1
AND tsn.category_id = #{categoryId,jdbcType=BIGINT}
AND tsn.norm_name LIKE CONCAT(CONCAT('%',#{key}),'%')
order by tsn.create_time desc
limit #{start},#{length}

SELECT tsn.* FROM t_standard_norm AS tsn WHERE 1=1
AND tsn.category_id = #{categoryId,jdbcType=BIGINT}
AND tsn.norm_name = #{normName,jdbcType=VARCHAR}
AND tsn.oid != #{oid,jdbcType=BIGINT}
ORDER BY tsn.oid DESC LIMIT 1

mapper.delete:
delete from t_standard_norm
where oid in

  #{id}

delete from t_standard_norm
where category_id in

  #{id}
