mapper.select:
select ss.student_id, s.name as student_name, g.grade_name, c.name as class_name, t.name as class_teacher, ss.score from t_stu_score ss left join t_student s on s.id = ss.student_id left join t_grade g on g.oid = s.grade_id left join t_class c on s.class_id = c.id left join t_teacher t on t.id = c.teacher_id
ss.score >= #{startScore} and
ss.score > 0 and
ss.score <= #{endScore}
c.id = #{userClassId}
and s.name like concat('%', #{query.studentName}, '%')
and s.student_no like concat('%', #{query.studentNo}, '%')
and g.oid = #{query.gradeId}
and c.name like concat('%', #{query.className}, '%')
order by score

select s.id, s.name as student_name, s.student_no, g.grade_name, c.name as class_name, t.name as class_teacher, f.faculty_name, m.major_name, ss.score from t_student s left join t_stu_score ss on ss.student_id = s.id left join t_grade g on g.oid = s.grade_id left join t_class c on c.id = s.class_id left join t_teacher t on t.id = c.teacher_id left join t_faculty f on f.oid = c.faculty_id left join t_major m on m.oid = s.major_id
s.status = 1 and s.is_valid = 1
and s.name like concat('%',#{query.studentName},'%')
and s.student_no like concat('%',#{query.studentNo},'%')
and g.oid = #{query.gradeId}
and c.name like concat('%',#{query.className},'%')
and f.oid = #{query.facultyId}
and m.oid = #{query.majorId}
order by score desc

select c.id, c.name as class_name, t.name as class_teacher, g.grade_name, f.faculty_name, m.major_name, sum(ss.score) as score from t_class c left join t_student s on s.class_id = c.id left join t_stu_score ss on ss.student_id = s.id left join t_grade g on g.oid = s.grade_id left join t_teacher t on t.id = c.teacher_id left join t_faculty f on f.oid = c.faculty_id left join t_major m on m.oid = s.major_id
and c.name like concat('%',#{query.className},'%')
and g.oid = #{query.gradeId}
and f.oid = #{query.facultyId}
and m.oid = #{query.majorId}
group by c.id order by score desc
