mapper.update:
update t_user_role
set user_id = #{userId,jdbcType=BIGINT},
  role_id = #{roleId,jdbcType=BIGINT},
  year_id = #{yearId,jdbcType=BIGINT},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where oid = #{oid,jdbcType=BIGINT}

mapper.select:
select count(tur.oid) from t_user_role as tur where 1=1
and tur.user_id = #{userId,jdbcType=BIGINT}
AND (tur.year_id = #{yearId,jdbcType=BIGINT} OR tur.year_id IS NULl)

select distinct tur.role_id, tur.oid, tur.user_id, tur.year_id, tur.create_time from t_user_role as tur where 1=1
and tur.user_id = #{userId,jdbcType=BIGINT}
AND (tur.year_id = #{yearId,jdbcType=BIGINT} OR tur.year_id IS NULl)
order by tur.oid desc
limit #{start},#{length}

SELECT distinct tur.role_id FROM t_user_role AS tur WHERE tur.user_id = #{userId,jdbcType=BIGINT}
AND (tur.year_id = #{yearId,jdbcType=BIGINT} OR tur.year_id IS NULl)
ORDER BY tur.role_id ASC

mapper.delete:
DELETE
FROM
  t_user_role
WHERE
  user_id = #{userId,jdbcType=BIGINT}
  AND role_id = #{roleId,jdbcType=BIGINT}

  AND year_id = #{yearId,jdbcType=BIGINT}

DELETE
FROM
t_user_role
WHERE
user_id = #{userId,jdbcType=BIGINT}
AND role_id = #{roleId,jdbcType=BIGINT}
AND year_id = #{yearId,jdbcType=BIGINT}

DELETE tur.*
FROM
  t_user_role AS tur
  LEFT JOIN t_assess_range AS tar ON tar.user_id = tur.user_id
WHERE
  tar.year_id = #{yearId,jdbcType=BIGINT}
  AND tur.role_id = #{roleId,jdbcType=BIGINT}
  AND tar.dept_id = #{deptId,jdbcType=BIGINT}

mapper.update:
UPDATE t_user_role AS tur
SET tur.year_id = #{yearId,jdbcType=BIGINT}
WHERE
  tur.user_id = #{userId,jdbcType=BIGINT}
  AND tur.role_id = #{roleId,jdbcType=BIGINT}
  AND tur.year_id IS NULL
