mapper.select:
SELECT ty.* FROM t_year AS ty WHERE 1=1
AND ty.year_name = #{yearName,jdbcType=VARCHAR}
AND ty.oid != #{oid,jdbcType=BIGINT}
ORDER BY ty.oid DESC LIMIT 1

mapper.delete:
delete from t_year
where oid in

    #{id}

mapper.update:
update t_year
set curr = 1
where oid = #{oid,jdbcType=BIGINT};
update t_year
set curr = 2
where oid != #{oid,jdbcType=BIGINT};

mapper.select:
select *
from t_year
where year_start = #{startTime}
  and year_end = #{endTime}

mapper.update:
update t_year
set curr = 1
where oid = #{oid}

mapper.select:
select oid
from t_year
where year_start between #{startTime} and #{endTime}
